import React from 'react'
import './question.css'
import { nanoid } from 'nanoid'

export default function Question(props) {

	const shuffledAnswers = [...props.incorrect_answers, props.correct_answer].sort(() => Math.random() - 0.5)

	const sortedAnswers = shuffledAnswers.map((answer) => (
		<button className='answer' key={nanoid()} onClick={() => props.handleQuestionClick(props.id, props.question, answer)}>
			{answer}
		</button>
	))

	return (
		<div className='question--wrapper7'>
			<h1 className='question'>{props.question}</h1>
			<h2 className='category'>{props.category}</h2>
			<div className='answers'>{sortedAnswers}</div>
		</div>
	)
}
