import React from 'react'
import './App.css'
import Question from './components/Question'
import Start from './components/Start'
import Options from './components/Options'
import { nanoid } from 'nanoid'

export default function App() {
	const [questions, setQuestions] = React.useState([])
	const [selectedAnswers, setSelectedAnswers] = React.useState([])
	const [isChecked, setIsChecked] = React.useState(false)
	const [options, setOptions] = React.useState({
		difficulty: '',
		category: '11',
		amount: '2',
	})



	    //  let qs = data.results.map((item, i) => {
		// 					const entities = {
		// 						'&#039;': "'",
		// 						'&quot;': '"',
		// 						'&ntilde;': 'ñ',
		// 						'&eacute;': 'é',
		// 						'&amp;': '&',
		// 						'&uuml;': 'ü',
		// 					}
		// 					const pItems = data.results[i].incorrect_answers.map((item, j) => (
		// 						<div>
		// 							<input type='radio' name={'question' + i} value={data.results[i]} /> <p key={j.toString()}>{item.replace(/&#?\w+;/g, (match) => entities[match])}</p>
		// 						</div>
		// 					))

		// 					return (
		// 						<div key={i.toString()}>
		// 							<div>
		// 								{data.results[i].question.replace(/&#?\w+;/g, (match) => entities[match])}
		// 								<br />
		// 								<br />
		// 								<div>
		// 									{pItems}
		// 									<input type='radio' name={'question' + i} value='correct_answer' />
		// 									<p> {data.results[i].correct_answer.replace(/&#?\w+;/g, (match) => entities[match])}</p>
		// 								</div>
		// 								<br />
		// 							</div>
		// 						</div>
		// 					)
		// 				})

	// get data from api and return it
	function getQuestions() {
		// fetch from api
		fetch('https://opentdb.com/api.php?amount=' + options.amount + '&category=' + options.category + '&difficulty=' + options.difficulty)
			.then((response) => response.json())
			.then((data) => {
				data.results.forEach((question) => {
					question.incorrect_answers.push(question.correct_answer)
					question.incorrect_answers = question.incorrect_answers.sort()
				})
					const entities = {
						'&#039;': "'",
						'&quot;': '"',
						'&ntilde;': 'ñ',
						'&eacute;': 'é',
						'&amp;': '&',
						'&uuml;': 'ü',
					}
				setQuestions(data.results.replace((match) => entities[match]))
			})
			.catch((error) => console.log(error))
	}

	function handleQuestionClick(questionId, question, answer) {
		if (!isChecked) {
			setSelectedAnswers((prevState) => {
				const newState = { ...prevState }
				newState[questionId] = answer
				return newState
			})
		}
	}

	React.useEffect(() => {
		getQuestions()
	}, [])

	// compare answers with correct answer
	function checkAnswers() {
		setIsChecked(true)
		const correctAnswers = questions.map((question) => question.correct_answer)
		// for each selectedAnswer, if is in correctAnswers, set points +1
		// if not, set points -1
		const points = Object.keys(selectedAnswers).reduce((points, key) => {
			if (correctAnswers.includes(selectedAnswers[key])) {
				return points + 1
			} else {
				return
			}
		}, 0)

		// console.log(selectedAnswers)
		// const numberOfCorrectAnswers = correctAnswers.map((answers) => (selectedAnswers.includes(answers) ? 1 : 0))
		// console.log(numberOfCorrectAnswers)
		//const correct = correctAnswers.filter((answer) => selectedAnswers[answer] === answer)
		// console.log(correct.length)
	}

	// map questions to question components
	console.log(questions)
	return (
		<div className='App'>
			<Start />
			<Options />
			{questions.map((question) => (
				<Question
					key={nanoid()}
					id={questions.indexOf(question)}
					question={question.question}
					category={question.category}
					correct_answer={question.correct_answer}
					incorrect_answers={question.incorrect_answers}
					answers={question.answers}
					handleQuestionClick={handleQuestionClick}
					selectedAnswer={selectedAnswers[questions.indexOf(question)]}
					isChecked={isChecked}
				/>
			))}
			<button className='check' onClick={checkAnswers}>
				Check Answers
			</button>
		</div>
	)
}
