import './question.css'
import Answer from './Answer'
import './answer.css'

export default function Question(props) {
	console.log(props)

	// randomize the answers
	const allAnswers = props.incorrect_answers.concat(props.correct_answer)
	allAnswers = allAnswers.sort(() => Math.random() - 0.5)

	// map allAnswers to Answer components
	const answers = allAnswers.map((answer) => <Answer answer={answer} key={answer} />)
	// const answers = allAnswers.map((answer) =>
	// <Answer answer={answer}
	// key={answer}
	// correct_answer={props.correct_answer}
	// onClick={props.onClick} />
	// )

	return (
		<div>
			<h1 className='question'>{props.question}</h1>
			<h2 className='category'>{props.category}</h2>
			{answers}
		</div>
	)
}
