import React from 'react'
import './App.css'
import Question from './components/Question'
import Start from './components/Start'
import Options from './components/Options'
import Results from './components/Results'
import { nanoid } from 'nanoid'

export default function App() {
	const [questions, setQuestions] = React.useState([])
	const [selectedAnswers, setSelectedAnswers] = React.useState([])
	const [isChecked, setIsChecked] = React.useState(false)
	const [options, setOptions] = React.useState({
		visible: true,
		difficulty: '',
		category: '',
		amount: '1',
		type: '',
	})
	const [score, setScore] = React.useState({})
	const [turn, setTurn] = React.useState(0)

	// console.log(score)
	// get data from api and return it
	function getQuestions() {
		// fetch from api
		fetch(
			'https://opentdb.com/api.php?amount=' + options.amount + '&category=' + options.category + '&difficulty=' + options.difficulty + '&type=' + options.type
		)
			.then((response) => response.json())
			.then((data) => {
				data.results.forEach((question) => {
					question.incorrect_answers.push(question.correct_answer)
					question.incorrect_answers = question.incorrect_answers.sort()
				})
				setQuestions(data.results)
			})
			.catch((error) => console.log(error))
	}
	function handleOptionsChange(event) {
		setOptions((prevOptions) => {
			return {
				...prevOptions,
				[event.target.name]: event.target.value,
			}
		})
		console.log(options)
	}
	// function handleChange(event) {
	// 	console.log(event)
	// 	const { name, value, type, checked } = event.target
	// 	setFormData((prevFormData) => {
	// 		return {
	// 			...prevFormData,
	// 			[name]: type === 'checkbox' ? checked : value,
	// 		}
	// 	})
	// }

	function handleQuestionClick(questionId, answer) {
		if (!isChecked) {
			setSelectedAnswers((prevState) => {
				const newState = { ...prevState }
				newState[questionId] = answer
				return newState
			})
		}
	}

	React.useEffect(() => {
		getQuestions()
	}, [])

	// compare answers with correct answer
	function checkAnswers() {
		setIsChecked(true)
		const correctAnswers = questions.map((question) => question.correct_answer)
		let count = 0
		Object.keys(selectedAnswers).map((key) => {
			if (selectedAnswers[key] === correctAnswers[key]) {
				return count++
			}
		})
		setScore((prevState) => {
			const newState = { ...prevState }
			newState[turn] = {
				turn: turn,
				correctsAnswers: count,
				questions: questions.length,
			}
			return newState
		})
	}

	function restartGame() {
		setTurn((prevTurn) => prevTurn + 1)
		setIsChecked(false)
		setSelectedAnswers([])
		getQuestions()
	}

	// map questions to question components
	// console.log(questions)

	// take all scores from score object and add them in a total score const
	const totalScore = Object.values(score).reduce((acc, curr) => {
		return acc + curr.correctsAnswers
	}, 0)
	const totalQuestions = Object.values(score).reduce((acc, curr) => {
		return acc + curr.questions
	}, 0)
	const totalTurns = Object.keys(score).length

	return (
		<div className='App'>
			<Start />
			<Options options={options} handleOptionsChange={handleOptionsChange} />
			{questions.map((question) => (
				<Question
					key={nanoid()}
					id={questions.indexOf(question)}
					question={question.question}
					category={question.category}
					correct_answer={question.correct_answer}
					incorrect_answers={question.incorrect_answers}
					answers={question.answers}
					handleQuestionClick={handleQuestionClick}
					selectedAnswer={selectedAnswers[questions.indexOf(question)]}
					isChecked={isChecked}
				/>
			))}
			<button className='check' onClick={isChecked ? restartGame : checkAnswers}>
				{isChecked ? 'Restart' : 'Check answers'}
			</button>
			<Results score={totalScore} totalQuestions={totalQuestions} totalTurns={totalTurns} />
		</div>
	)
}
