import React from 'react'
import './App.css'
import './Question'

function App() {
	const [questions, setQuestions] = React.useState([])

	function getData() {
		fetch('https://opentdb.com/api.php?amount=5')
			.then((res) => res.json())
			.then((data) => console.log(data))
	}

	React.useEffect(() => {
		setQuestions(getData())
		console.log(questions)
	}, [])

	const question = questions.map((question) => {
		return (
			<div>
				<h1>{question.result.question}</h1>
				<h2>{question.result.correct_answer}</h2>
				<h2>{question.result.incorrect_answers}</h2>
			</div>
		)
	})

	return (
		<div className='App'>
			Hello
			<Question />
		</div>
	)
}

export default App
