import React from 'react'
import './start.css'

export default function Start(props) {
	const numberOfQuestions = props.options.amount + props.options.amount == 1 ? `1 question` : `questions`
	return (
		<div className='start'>
			<h1>Welcome to the Quiz!</h1>
			<h2>Click the button below to start the quiz!</h2>
			<small>
				You will play rounds of {numberOfQuestions} in {props.getCategory(props.options.category)} category with {props.options.difficulty} difficulty.{' '}
				<a onClick={props.toggleOptions}>(change options)</a>
			</small>
			<button>Start Quiz</button>
		</div>
	)
}
