import React from 'react'
import './question.css'
import { nanoid } from 'nanoid'

export default function Question(props) {
	const [shuffledAnswers, setShuffledAnswers] = React.useState([...props.incorrect_answers].concat(props.correct_answer))

	React.useEffect(() => {
		setShuffledAnswers((prevState) => {
			const newState = [...prevState]
			return shuffle(newState)
		})
	}, [props.isChecked])

	function shuffle(array) {
		let currentIndex = array.length,
			temporaryValue,
			randomIndex
		while (0 !== currentIndex) {
			randomIndex = Math.floor(Math.random() * currentIndex)
			currentIndex -= 1
			temporaryValue = array[currentIndex]
			array[currentIndex] = array[randomIndex]
			array[randomIndex] = temporaryValue
		}
		return array
	}

	const sortedAnswers = shuffledAnswers.map((answer) => (
		<button
			className={`answer ${props.selectedAnswer === answer ? 'selected' : ''} ${answer === props.correct_answer && props.isChecked ? 'correct' : ''} ${
				answer !== props.correct_answer && props.isChecked ? 'incorrect' : ''
			}`}
			key={nanoid()}
			onClick={() => props.handleQuestionClick(props.id, props.question, answer)}
		>
			{answer}
		</button>
	))

	return (
		<div className='question--wrapper'>
			<h1 className='question'>{props.question}</h1>
			<h2 className='category'>{props.category}</h2>
			<div className='answers'>
				<div className='answers'>{sortedAnswers}</div>
			</div>
		</div>
	)
}
