import './question.css'
import Answer from './Answer'
import './answer.css'

export default function Question(props) {
	console.log(props)

	// randomize the answers
	const allAnswers = props.incorrect_answers.concat(props.correct_answer)
	const shuffledAnswers = answers.sort(() => Math.random() - 0.5)

	const answers = []

	// push shuffled answers into answers array
	for (let i = 0; i < shuffledAnswers.length; i++) {
		answers.push(<Answer key={i} answer={shuffledAnswers[i]} correctAnswer={props.correct_answer} question={props.question} category={props.category} />)
	}

	return (
		<div>
			<h1 className='question'>{props.question}</h1>
			<h2 className='category'>{props.category}</h2>
		</div>
	)
}
