import './question.css'
import { nanoid } from 'nanoid'

export default function Question(props) {
	// randomize the answers
	const allAnswers = props.incorrect_answers.concat(props.correct_answer)
	const shuffledAnswers = allAnswers.sort(() => Math.random() - 0.5)

	React.useEffect(() => {
		const answers = shuffledAnswers.map((answer) => (
			<button className='answer' key={nanoid()} onClick={() => props.handleQuestionClick(props.id, props.question, answer)}>
				{answer}
			</button>
		))
	}, [])

	return (
		<div className='question--wrapper7'>
			<h1 className='question'>{props.question}</h1>
			<h2 className='category'>{props.category}</h2>
			<div className='answers'>{answers}</div>
		</div>
	)
}
