import React from 'react'
import './App.css'
import Question from './components/Question'
import { nanoid } from 'https://cdn.jsdelivr.net/npm/nanoid/nanoid.js'

export default function App() {
	const [questions, setQuestions] = React.useState([])

	// get data from api and return it
	function getQuestions() {
		fetch('https://opentdb.com/api.php?amount=5')
			.then((res) => res.json())
			.then((data) => setQuestions(data.results))
			.catch((err) => console.log(err))
	}

	function handleQuestionClick(question, answer) {
		console.log(question)
		console.log(answer)
	}

	// add a unique key to questions
	const questionsWithKey = questions.map((question) => {
		return {
			...question,
			key: nanoid(),
		}
	})

	React.useEffect(() => {
		getQuestions()
		setQuestions(questionsWithKey)
	}, [])

	console.log(questionsWithKey)
	// map questions to question components

	return (
		<div className='App'>
			{questions.map((question) => (
				<Question
					key={question.key}
					id={questions.indexOf(question)}
					question={question.question}
					category={question.category}
					correct_answer={question.correct_answer}
					incorrect_answers={question.incorrect_answers}
					handleQuestionClick={handleQuestionClick}
				/>
			))}
		</div>
	)
}
