import react from 'react'
import './App.css'

function App() {
	const [questions, setQuestions] = React.useState([])

	function getData() {
		fetch('https://opentdb.com/api.php?amount=5')
			.then((res) => res.json())
			.then((data) => console.log(data))
	}

	setQuestions(getData())

	const question = questions.map((question) => {
		return (
			<div>
				<h1>{question.question}</h1>
				<h2>{question.correct_answer}</h2>
				<h2>{question.incorrect_answers}</h2>
			</div>
		)
	})

	return <div className='App'>{questions}</div>
}

export default App
