import React from 'react'
import './App.css'
import Question from './components/Question'
import { nanoid } from 'nanoid'

export default function App() {
	const [questions, setQuestions] = React.useState([])
	const [selectedAnswers, setSelectedAnswers] = React.useState([])

	// get data from api and return it
	function getQuestions() {
		fetch('https://opentdb.com/api.php?amount=5')
			.then((res) => res.json())
			.then((data) => setQuestions(data.results))
			.catch((err) => console.log(err))
	}

	function handleQuestionClick(questionId, question, answer) {
		setSelectedAnswers((prevState) => {
			const newState = { ...prevState }
			newState[questionId] = answer
			return newState
		})
	}

	React.useEffect(() => {
		getQuestions()
	}, [])

	const allAnswers = props.incorrect_answers.concat(props.correct_answer)
	const shuffledAnswers = allAnswers.sort(() => Math.random() - 0.5)

	React.useEffect(() => {
		const answers = shuffledAnswers.map((answer) => (
			<button className='answer' key={nanoid()} onClick={() => props.handleQuestionClick(props.id, props.question, answer)}>
				{answer}
			</button>
		))
	}, [])

	// map questions to question components

	return (
		<div className='App'>
			{questions.map((question) => (
				<Question
					key={nanoid()}
					id={questions.indexOf(question)}
					question={question.question}
					category={question.category}
					correct_answer={question.correct_answer}
					incorrect_answers={question.incorrect_answers}
					handleQuestionClick={handleQuestionClick}
					selectedAnswers={selectedAnswers}
				/>
			))}
		</div>
	)
}
