import './question.css'
import Answer from './Answer'
import './answer.css'

export default function Question(props) {
	// randomize the answers
	const allAnswers = props.incorrect_answers.concat(props.correct_answer)
	const answers = allAnswers.sort(() => Math.random() - 0.5)

	return (
		<div className='question--wrapper7' onClick={props.handleQuestionClick}>
			<h1 className='question'>{props.question}</h1>
			<h2 className='category'>{props.category}</h2>
			<div className='answers'>
				{answers.map((answer) => (
					<Answer handleQuestionClick={(event) => props.handleQuestionClick(event, props.answer)} answer={answer} key={answer} correct_answer={props.correct_answer} onClick={props.onClick} />
				))}
			</div>
		</div>
	)
}
